import axios from 'axios';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useToken } from '../auth/useToken';
import { EmailVerficationFail } from './EmailVerficationFail';
import { EmailVerficationSuccess } from './EmailVerficationSuccess';

export const EmailVerfication = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [isSuccess, setIsSuccess] = useState(false);
  const [, setToken] = useToken();
  const { verificationCode } = useParams();

  useEffect(() => {
    const verifyEmail = async () => {
      setIsLoading(true);
      try {
        const response = await axios.put(
          `http://localhost:8080/api/verify-email`,
          {
            verificationCode,
          }
        );
        const { token } = response.data;
        setToken(token);
        setIsSuccess(true);
      } catch (error) {
        setIsSuccess(false);
      } finally {
        setIsLoading(false);
      }
    };
    verifyEmail();
  }, [verificationCode]);

  if (isLoading) return <div>Loading...</div>;
  if (!isSuccess) return <EmailVerficationFail />;
  return <EmailVerficationSuccess />;
};
